import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './Components/Header.js';
import Menu from './Components/Menu.js';
import Seccion from './Components/Seccion.js';
import Footer from './Components/Footer.js';
import './App.css';

const App = () => {
  return (
    <Router>
      <Header /> {/* El encabezado y menú */}
      <main>
        <Routes>
          <Route path="/" element={<Seccion titulo="Bienvenido a Autos 360" contenido="Aquí encontrarás los mejores autos." />} />
          <Route path="/autos" element={<SeccionAutos />} />
          <Route path="/contacto" element={<SeccionContacto />} />
        </Routes>
      </main>
      <Footer /> {/* El pie de página */}
    </Router>
  );
}

// Componente para mostrar los autos
const SeccionAutos = () => {
  const autos = [
    {
      id: 1,
      nombre: "Toyota Corolla",
      precio: "$20,000",
      imagen: "https://via.placeholder.com/150?text=Toyota+Corolla"
    },
    {
      id: 2,
      nombre: "Ford Mustang",
      precio: "$30,000",
      imagen: "https://via.placeholder.com/150?text=Ford+Mustang"
    },
    {
      id: 3,
      nombre: "Chevrolet Camaro",
      precio: "$28,000",
      imagen: "https://via.placeholder.com/150?text=Chevrolet+Camaro"
    }
  ];

  return (
    <div>
      <h2>Nuestros Autos</h2>
      <p>Explora nuestra gama de autos nuevos y usados:</p>
      <div className="autos-list">
        {autos.map((auto) => (
          <div key={auto.id} className="auto-item">
            <img src={auto.imagen} alt={auto.nombre} />
            <h3>{auto.nombre}</h3>
            <p>Precio: {auto.precio}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

// Nuevo componente para la sección de contacto
const SeccionContacto = () => {
  const [email, setEmail] = React.useState('');
  const [phone, setPhone] = React.useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(`Correo: ${email}, Teléfono: ${phone}`);
    // Aquí podrías enviar los datos a un servidor si lo deseas
  };

  return (
    <div>
      <h2>Contacto</h2>
      <p>Por favor, ingresa tu información de contacto:</p>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input 
            type="email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            required 
          />
        </div>
        <div>
          <label>Teléfono:</label>
          <input 
            type="tel" 
            value={phone} 
            onChange={(e) => setPhone(e.target.value)} 
            required 
          />
        </div>
        <button type="submit">Enviar</button>
      </form>
    </div>
  );
}

export default App;
