import React from 'react'; 

const Footer = () => {
  // Estilos en línea para el Footer
  const footerStyle = {
    backgroundColor: '#333',
    color: '#fff',
    textAlign: 'center',
    padding: '10px 0',
    fontSize: '1rem',
    position: 'absolute',  // Cambié esto de 'relative' a 'absolute'
    bottom: '0',  // Esto asegura que el pie de página se quede en la parte inferior
    width: '100%',
  };

  // Estilo para el contenedor principal para asegurarse de que se estire a la altura de la pantalla
  const containerStyle = {
    minHeight: '100vh',  // Esto asegura que el contenido ocupe al menos toda la altura de la pantalla
    display: 'flex',
    flexDirection: 'column',
  };

  return (
    <div style={containerStyle}>
      {/* Aquí va el contenido principal de la página */}
      <div style={{ flex: 1 }}>
        {/* Contenido principal */}
      </div>
      <footer style={footerStyle}>
        <p>Nissan GTR R35 © 2024</p>
      </footer>
    </div>
  );
};

// footer.js
window.addEventListener('scroll', function() {
  var footer = document.getElementById('footer');
  
  // Obtenemos la posición actual del scroll
  var scrollPosition = window.scrollY;
  
  // Calculamos la nueva posición para el footer
  footer.style.position = 'absolute'; // Asegura que sea movible
  footer.style.top = scrollPosition + window.innerHeight - footer.offsetHeight + 'px';
});

// footer.js
window.addEventListener('DOMContentLoaded', function() {
  var footer = document.getElementById('footer');
  
  // Verificamos si el footer existe antes de aplicar el estilo
  if (footer) {
      window.addEventListener('scroll', function() {
          var scrollPosition = window.scrollY;
          footer.style.position = 'absolute'; // Asegura que sea movible
          footer.style.top = scrollPosition + window.innerHeight - footer.offsetHeight + 'px';
      });
  }
});




export default Footer;
